-- CRP indicator types.

CREATE TABLE `crp_indicator_types` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;


-- CRP Indicators
CREATE TABLE `crp_indicators` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `serial` varchar(5) NOT NULL,
  `name` text NOT NULL,
  `description` text NOT NULL,
  `is_active` tinyint(1) NOT NULL,
  `indicator_type_id` int(11) NOT NULL,
  `date_added` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `crp_indicator_type_id_fk` (`indicator_type_id`),
  CONSTRAINT `FK_crp_indicator_types_indicator_type_id` FOREIGN KEY (`indicator_type_id`) REFERENCES `crp_indicator_types` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8;


-- CRP reported targets
CREATE TABLE `crp_indicator_reports` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `target` text,
  `next_target` text,
  `actual` text,
  `support_links` text,
  `deviation` text,
  `liaison_institution_id` int(11) NOT NULL,
  `indicator_id` int(11) NOT NULL,
  `year` int(11) NOT NULL,
  `last_update` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `acronym` text,
  PRIMARY KEY (`id`),  
  KEY `FK_indicator_id_fk` (`indicator_id`),  
  CONSTRAINT `FK_crp_indicators_indicator_id` FOREIGN KEY (`indicator_id`) REFERENCES `crp_indicators` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=2331 DEFAULT CHARSET=utf8;

