INSERT INTO `crp_indicator_reports` (`id`,`target`,`next_target`,`actual`,`support_links`,`deviation`,`liaison_institution_id`,`indicator_id`,`year`,`last_update`,`acronym`) VALUES (1096,NULL,NULL,'15','Global Futures for Agriculture Project\r\n\r\nGlobal Futures project scientists work with breeders, plant physiologists, and agronomists to identify potentially promising technical traits and ‘design’ an ideal crop that is genetically tailored to reap bountiful harvests in specific environmental conditions (weather, soil quality/composition, limited resources, etc.) in that specific areas of the world are experiencing or are projected to experience due to climate change. These virtual crops are then being fed into IFPRI’s IMPACT model to determine their potential impact on yields in the future. The results reveal the extent to which new technologies can address food security and climate change challenges.\r\n\r\nhttp://globalfuturesproject.org/virtual-ag-technologies/ (the site will be re-designed in 2014)','',26,17,2013,'2014-02-21 17:20:51','Theme 4.3');
