INSERT INTO `crp_indicator_reports` (`id`,`target`,`next_target`,`actual`,`support_links`,`deviation`,`liaison_institution_id`,`indicator_id`,`year`,`last_update`,`acronym`) VALUES (1120,NULL,NULL,'29','Agriculture and Climate Change Policies were analyzed in 29 countries in West, East and Southern Africa, as part of the African Agriculture and Climate Change Series.\r\n\r\nhttp://www.ifpri.org/publication/west-african-agriculture-and-climate-change\r\nhttp://www.ifpri.org/publication/east-african-agriculture-and-climate-change-0\r\nhttp://www.ifpri.org/publication/southern-african-agriculture-and-climate-change\r\n\r\nAll 29 country summaries can be found:\r\nhttp://www.ifpri.org/publications/results/taxonomy%3A933.7013','',26,27,2013,'2014-02-21 17:46:23','Theme 4.3');
